mapper.select:
SELECT enrol_course_no as enrolCourseNo , enrol_course_course_name as enrolCourseCourseName , enrol_course_completion_division as enrolCourseCompletionDivision , enrol_course_course_credit as enrolCourseCourseCredit , enrol_course_dept_name as enrolCourseDeptName , enrol_course_school_year as enrolCourseSchoolYear , enrol_course_class as enrolCourseClass , enrol_course_prof_name as enrolCourseProfName , enrol_course_classroom as enrolCourseClassroom , enrol_course_year as enrolCourseYear , enrol_course_semester as enrolCourseSemester
FROM haksa_enrol_course
WHERE enrol_course_student_number = #{id}
